import React from 'react';
import { PieChart } from 'react-minimal-pie-chart';
import './css/style.css';


export default class App extends React.Component {

  state = {
    status: null,
    loading: true,
    status_code: null,
    room_number: null,
    building_number: null,
    max_capacity: null,
    curr_capacity: null
  };

  async componentDidMount() {
    const url = "https://${api_gateway.restApiId}.execute-api.${self.region}.amazonaws.com/prod/";

    const response = await fetch(url, {mode: 'no-cors', method: 'get'});

    const data = await response.json();

    this.setState({
      status_code: data.status_code,
      building_number: data.building_number, 
      room_number: data.room_number,
      max_capacity: data.max_capacity,
      curr_capacity: data.curr_capacity,
      status: data.status
    });

    console.log(data);
  }


  colorpicker = (status) => {
    const green = "#5af542";
    const yellow = "#fff23d";
    const red = "#ff2605";
    if (this.status.status === "red"){
      document.getElementById("color-code-box").style.color = red;
    }
    else if (this.status.status === "yellow") {
      document.getElementById("color-code-box").style.color = yellow;
    }
    else {
      document.getElementById("color-code-box").style.color = green;
    }
  } 

  render() {

    return (

      <div>

        {this.state.loading || 
        !this.building_number || 
        !this.room_number ||
        !this.status ||
        !this.status_code ||
        !this.max_capacity ||
        !this.curr_capacity
         ? (<div id="loading">loading</div>) : 
        (<div class="main-page">
          <h1 id="main-head">Room Capacity Checkr</h1>

          <h2 class="sub-head">Building: {this.state.building_number} </h2>
          <h2 class="sub-head">Room number: </h2>
          <h2 class="color-code-head">Color code: </h2>
          <div id="color-code-box" ></div>

          <h2 class="color-code-head">Occupancy: </h2> <p>{this.state.curr_capacity}</p>
          <h2 class="color-code-head">Status Code: </h2> <p>{this.state.status_code}</p>

          <div class="occupancy-pie-chart">
            <PieChart 
              animate={true}
              label={({ dataEntry }) => `${Math.round(dataEntry.percentage)} %`}
              data={[
                { title: 'Occupied spots', value: 10, color: '#fa0000'}, //occupied
                { title: 'Free spots', value: 15, color: '#3efa00' }, //free
              ]}
            />
          </div>
        </div>)
        }

      </div>

    );
  }

}
